mapper.select:
SELECT a.id AS "id", a.created_time AS "createdTime", a.sn AS "sn", a.inviter AS "inviter", a.invitee AS "invitee", a.invite_time AS "inviteTime", a.invite_type AS "inviteType", a.date_type_id AS "dateTypeId", a.gift_id AS "giftId", a.longitude AS "longitude", a.latitude AS "latitude", a.province AS "province", a.city AS "city", a.district AS "district", a.street AS "street", a.streetNumber AS "streetNumber", a.status AS "status", a.invite_way AS "inviteWay", a.content AS "content", a.shop AS "shop", ST_Distance_Sphere ( Point ( ${paramCondition.longitude}, ${paramCondition.latitude} ), Point ( a.longitude, a.latitude ) ) AS distance FROM qx_invite a INNER JOIN qx_user b ON a.inviter = b.id WHERE ST_Contains (ST_MakeEnvelope (Point ( ( ${paramCondition.longitude}+ ( ${paramCondition.inviteRange} / 111 ) ), ( ${paramCondition.latitude}+ ( ${paramCondition.inviteRange} / 111 ) ) ), Point ( ( ${paramCondition.longitude}- ( ${paramCondition.inviteRange} / 111 ) ), ( ${paramCondition.latitude}- ( ${paramCondition.inviteRange} / 111 ) ) ) ), Point ( a.longitude, a.latitude )) and a.content like '%${paramCondition.content}%' AND a.status = '0' AND a.invite_way = '1'
AND a.invite_type = #{paramCondition.inviteType}
AND a.date_type_id = #{paramCondition.dateTypeId}
AND b.sex = #{paramCondition.sex}
ORDER BY
a.invite_time
distance asc
